import './App.css';
import {useState, useEffect} from 'react';
import React from 'react';
import axios from 'axios';
import '@fontsource/roboto/300.css';
import '@fontsource/roboto/400.css';
import '@fontsource/roboto/500.css';
import '@fontsource/roboto/700.css';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import { experimentalStyled as styled } from '@mui/material/styles';


/**
 * To-Do
 * 
 * 1. make it get the word every time the page loads
 * 
 */

function App() {

  const [word, setWord] = useState({firstLetter: "", secondLetter: "", thirdLetter: "", fourthLetter: "", fifthLetter: ""});

  useEffect(() => {
    axios.get('http://localhost:3001/hello')
      .then(response => {
        console.log(response.data);
        setWord(response.data.value);
      }).catch(error => {
        console.error(error);
      });
  }, []);

  const [row, setRow] = useState(0);

  const [letterColors, setLetterColors] = useState({'Q': '#9c9ca4', 'W': '#9c9ca4', 'E':'#9c9ca4', 'R': '#9c9ca4', 'T': '#9c9ca4', 'Y': '#9c9ca4', 'U':'#9c9ca4','I':'#9c9ca4','O':'#9c9ca4','P':'#9c9ca4','A':'#9c9ca4','S':'#9c9ca4','D':'#9c9ca4','F':'#9c9ca4','G':'#9c9ca4','H':'#9c9ca4','J':'#9c9ca4','K':'#9c9ca4','L':'#9c9ca4','Z':'#9c9ca4','X':'#9c9ca4','C':'#9c9ca4','V':'#9c9ca4','B':'#9c9ca4','N':'#9c9ca4','M':'#9c9ca4', 'ENTER':'#9c9ca4','DELETE':'#9c9ca4'})

  const [prevGuesses, setPrevGuesses] = useState({0: ['','','','',''], 1:['','','','',''],2: ['','','','',''],3: ['','','','',''],4: ['','','','',''],5: ['','','','','']})

  const Item = styled(Paper)(({theme}) => ({
    backgroundColor:  '#121213' ,
    elevation: 1,
    padding: theme.spacing(4),
    textAlign: 'center',
    color: 'white',
    width: 2,
    height: 2,
    fontSize: 20
  }))


  const [firstLetter, setFirstLetter] = useState('');
  const [secondLetter, setSecondLetter] = useState('');
  const [thirdLetter, setThirdLetter] = useState('');
  const [fourthLetter, setFourthLetter] = useState('');
  const [fifthLetter, setFifthLetter] = useState('');



  const handleLetter = (event) => {
    var letter = event.target.innerHTML;
    if(letter === 'DELETE')
    {
      deleteLetter(letter);
    }

    else if(letter === 'ENTER')
    {
      //handle the guess
      /**
       * change the focused row
       * make sure the numbers are still on the first row
       * change the colors of the boxes of the first row
       */
      checkGuess();
      var guesses = prevGuesses;
      guesses[row] = [firstLetter, secondLetter, thirdLetter, fourthLetter, fifthLetter];
      setPrevGuesses(guesses);
      setFirstLetter('');
      setSecondLetter('');
      setThirdLetter('');
      setFourthLetter('');
      setFifthLetter('');
      setRow(row+1);
      console.log("checking");
    }

    else 
    {
      if(firstLetter === '')
        setFirstLetter(letter);
      else if(secondLetter === '')
        setSecondLetter(letter);
      else if(thirdLetter === '')
        setThirdLetter(letter);
      else if(fourthLetter === '')
        setFourthLetter(letter);
      else
        setFifthLetter(letter);
    }
  }

  const deleteLetter = (letter) => {
    if(fifthLetter === '')
      {
        if(fourthLetter === '')
        {
          if(thirdLetter === '')
          {
            if(secondLetter === '')
            {
              setFirstLetter('');
            }

            else
            {
              setSecondLetter('');
            }
          }

          else
          {
            setThirdLetter('');
          }
        }

        else
        {
          setFourthLetter('');
        }

      }

    else
    {
      setFifthLetter('');
    }
  }

  function checkGuess() {
    var colors = letterColors;
    var numBulls = 0;
    const wordValues = Object.values(word);

    if(word.firstLetter === firstLetter.toLowerCase())
    {
      colors[firstLetter] = '#67a561';
      numBulls++;
    }

    else if(wordValues.includes(firstLetter.toLowerCase()))
    {
      colors[firstLetter] = '#cbaa36';
    }

    else
    {
      colors[firstLetter] = '#4c4c4c';
    }

    if(word.secondLetter === secondLetter.toLowerCase())
    {
      colors[secondLetter] = '#67a561';
      numBulls++;
    }

    else if(wordValues.includes(secondLetter.toLowerCase()))
    {
      colors[secondLetter] = '#cbaa36';
    }

    else
    {
      colors[secondLetter] = '#4c4c4c';
    }

    if(word.thirdLetter === thirdLetter.toLowerCase())
    {
      colors[thirdLetter] = '#67a561';
      numBulls++;
    }

    else if(wordValues.includes(thirdLetter.toLowerCase()))
    {
      colors[thirdLetter] = '#cbaa36';
    }

    else
    {
      colors[thirdLetter] = '#4c4c4c';
    }

    if(word.fourthLetter === fourthLetter.toLowerCase())
    {
      colors[fourthLetter] = '#67a561';
      numBulls++;
    }

    else if(wordValues.includes(fourthLetter.toLowerCase()))
    {
      colors[fourthLetter] = '#cbaa36';
    }

    else
    {
      colors[fourthLetter] = '#4c4c4c';
    }

    if(word.fifthLetter === fifthLetter.toLowerCase())
    {
      colors[fifthLetter] = '#67a561';
      numBulls++;
    }

    else if(wordValues.includes(fifthLetter.toLowerCase()))
    {
      colors[fifthLetter] = '#cbaa36';
    }

    else
    {
      colors[fifthLetter] = '#4c4c4c';
    }

    if(numBulls === 5)
    {
      console.log("congrats! you guessed the word");
    }
    setLetterColors(colors);
    //console.log(letterColors);
  }

  function KeyboardItem({letter}) {
    return(
      <button className = "KeyboardItem" style = {{backgroundColor: letterColors[letter]}}
        onClick = {handleLetter}>{letter}</button>
    )
  }

  function Keyboard() {
    return (
      <div className = "Keyboard">
