   */
  const getWord = async() =>{
    try {
      await fetch('http://localhost:3001/hello')
        .then(response => {
          response.json()
          .then(data => {
            console.log(data.value);
            setWord(data.value);
          })
        })
    }

    catch(error) {
      console.error(error);
    }
  }

